mapper.select:
   	 	SELECT *
		FROM (((Reserve_history INNER JOIN Room ON Reserve_history.room_no=room.room_no) INNER JOIN Building ON room.build_no = Building.build_no) INNER JOIN Employee ON Reserve_history.reserve_emp_no=Employee.emp_no) INNER JOIN department ON employee.dept_no = department.dept_no



				AND build_name=#{buildingSelect}


				AND dept_name = #{deptSelect}


				AND room_type =#{roomTypeSelect}


				AND     ((TO_DATE (#{timeSelectStart})<= start_date AND start_date <= TO_DATE (#{timeSelectEnd}))
                 		OR 
                		(TO_DATE (#{timeSelectStart})<= end_date AND end_date <= TO_DATE (#{timeSelectEnd})))



   		 ORDER BY reserve_no DESC

Select DISTINCT Room_no, countint, alltable.Room_name FROM
( SELECT room_name , Count(*) as countint FROM (((Reserve_history INNER JOIN Room ON Reserve_history.room_no=room.room_no) INNER JOIN Building ON room.build_no = Building.build_no) INNER JOIN Employee ON Reserve_history.reserve_emp_no=Employee.emp_no) INNER JOIN department ON employee.dept_no = department.dept_no
AND build_name=#{buildingSelect}
AND dept_name = #{deptSelect}
AND room_type =#{roomTypeSelect}
AND ((TO_DATE (#{timeSelectStart})<= start_date AND start_date <= TO_DATE (#{timeSelectEnd})) OR (TO_DATE (#{timeSelectStart})<= end_date AND end_date <= TO_DATE (#{timeSelectEnd})))
GROUP BY room_name) counttable
RIGHT OUTER JOIN
( SELECT Room.room_no, Room.room_name FROM (((Reserve_history INNER JOIN Room ON Reserve_history.room_no=Room.room_no) INNER JOIN Building ON room.build_no = Building.build_no) INNER JOIN Employee ON Reserve_history.reserve_emp_no=Employee.emp_no) INNER JOIN department ON employee.dept_no = department.dept_no
AND ((TO_DATE (#{timeSelectStart})<= start_date AND start_date <= TO_DATE (#{timeSelectEnd})) OR (TO_DATE (#{timeSelectStart})<= end_date AND end_date <= TO_DATE (#{timeSelectEnd})))
) alltable
ON counttable.room_name=alltable.room_name ORDER BY room_no
